import * as React from 'react';
import { View, Text, StyleSheet, ScrollView, ImageBackground, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

function PortalScreen({navigation}) {
  return (
    <View style={{ flex: 1, }}>

      <View style={{ flex: 0.3, }}>
        <ScrollView style={{flex:1}} contentContainerStyle={{width:'200%', height:250}} horizontal>
          <ImageBackground style={styles.image} source={{ uri: 'https://picsum.photos/200/300' }}>
            <TouchableOpacity 
            onPress={()=>navigation.navigate('Noticia', 
              {
              titulo: 'Um titulo de exemplo', 
              conteudo: 'Minha noticia de testes!'
              })} 
            style = {{
              width:'100%', 
              height:'100%', 
              backgroundColor:'rgba(0,0,0,0.4)', 
              justifyContent:'flex-end'
            }}>
              <Text style={{fontSize: 27, color:'white'}}>A manchete da Noticia</Text>
            </TouchableOpacity>
          </ImageBackground>
        </ScrollView>
      </View>

      <View style={{flex:0.7, padding:20}}>
        <View style={{
          width:50, 
          height:2, 
          backgroundColor:'#069', 
          position:'absolute', 
          top:40, 
          left:40
        }}></View>
        <Text>Mais Noticias</Text>

        <ScrollView contentContainerStyle={{padding:20}} style={{flex:1}}>
          <View style={{flexDirection:'row', marginBottom:10}}>
            <Image source={{ uri: 'https://picsum.photos/200/300'}}
              style={{width:100, height:100}}
            />
            <Text style={{padding:10}}>MInha noticia de teste</Text>
          </View>
        </ScrollView>

      </View>
    </View>
  );
}

function NoticiaScreen({route, navigation}) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>{route.params.titulo}</Text>
      <Text>{route.params.conteudo}</Text>
    </View>
  );
}

const Stack = createNativeStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Portal" component={PortalScreen} />
        <Stack.Screen name="Noticia" component={NoticiaScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;

const styles = StyleSheet.create({
  image: {
    flex: 1,
    resizeMode:'cover',
    justifyContent: 'flex-end',
    width: '50%'
  },
});
