import * as React from 'react';
import { View, Text, StyleSheet, ScrollView, ImageBackground } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

function PortalScreen() {
  return (
    <View style={{ flex: 1, }}>
      <ScrollView style={{flex:1}} contentContainerStyle={{width:'200%', height:250}} horizontal>
        <ImageBackground style={styles.image} source={{ uri: 'https://picsum.photos/200/300' }}>
          <View style={{position:'absolute', left:0, top:0, width:'100%', height:'100%', backgroundColor:'rgba(0,0,0,0.3)'}}></View>
          <Text style={{fontSize:29, color: 'white', zIndex:5}}>A minha not√≠cia!</Text>
        </ImageBackground>
      </ScrollView>
    </View>
  );
}

function NoticiaScreen() {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Details Screen</Text>
    </View>
  );
}

const Stack = createNativeStackNavigator();

function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Portal">
        <Stack.Screen name="Portal" component={PortalScreen} />
        <Stack.Screen name="Noticia" component={NoticiaScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

export default App;

const styles = StyleSheet.create({
  image: {
    flex: 1,
    resizeMode:'cover',
    justifyContent: 'flex-end',
    width: '50%'
  },
});
